mapper.select:
select o.scheduleId,o.FirstHomeWin satWin,o.FirstStandoff satFlat,o.FirstGuestWin satLose, format(ifnull(o.RealHomeWin,o.FirstHomeWin),2) endWin, format(ifnull(o.RealStandoff,o.FirstStandoff),2) endFlat, format(ifnull(o.RealGuestWin,o.FirstGuestWin),2) endLose, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore > s.GuestScore then 3 when s.HomeScore = s.GuestScore then 1 else 0 end result, case when o.FirstHomeWin - o.FirstGuestWin >= 0 THEN (case when (abs(max(od.HomeWin) - o.FirstHomeWin) < abs(min(od.HomeWin) - o.FirstHomeWin)) then abs(min(od.HomeWin) - o.FirstHomeWin) ELSE abs(max(od.HomeWin) - o.FirstHomeWin) END) ELSE (case when (abs(max(od.GuestWin) - o.FirstGuestWin) < abs(min(od.GuestWin) - o.FirstGuestWin)) then abs(min(od.GuestWin) - o.FirstGuestWin) ELSE abs(max(od.GuestWin) - o.FirstGuestWin) END) END odd from europeodds o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid LEFT JOIN europeoddsdetail od on od.OddsID = o.OddsID and od.ModifyTime < s.MatchTime where o.CompanyID = #{companyId} and s.MatchState = -1
and odd >= #{satOdds}
and odd < #{endOdds}
sct.type = #{matchType}
AND o.FirstHomeWin = like #{satWin} AND o.FirstGuestWin = like #{satLose} AND o.FirstStandoff = like #{satFlat} and o.OddsID != #{oddsId}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc limit 50

select o.scheduleId,o.FirstHomeWin satWin,o.FirstStandoff satFlat,o.FirstGuestWin satLose, format(ifnull(o.RealHomeWin,o.FirstHomeWin),2) endWin, format(ifnull(o.RealStandoff,o.FirstStandoff),2) endFlat, format(ifnull(o.RealGuestWin,o.FirstGuestWin),2) endLose, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore > s.GuestScore then 3 when s.HomeScore = s.GuestScore then 1 else 0 end result, case when o.FirstHomeWin - o.FirstGuestWin >= 0 THEN (case when (abs(max(od.HomeWin) - o.FirstHomeWin) < abs(min(od.HomeWin) - o.FirstHomeWin)) then abs(min(od.HomeWin) - o.FirstHomeWin) ELSE abs(max(od.HomeWin) - o.FirstHomeWin) END) ELSE (case when (abs(max(od.GuestWin) - o.FirstGuestWin) < abs(min(od.GuestWin) - o.FirstGuestWin)) then abs(min(od.GuestWin) - o.FirstGuestWin) ELSE abs(max(od.GuestWin) - o.FirstGuestWin) END) END odd from europeodds o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid LEFT JOIN europeoddsdetail od on od.OddsID = o.OddsID where o.CompanyID = #{companyId} and s.MatchState = -1
and odd >= #{satChange}
and odd < #{endChange}
sct.type = #{matchType}
AND o.FirstHomeWin = like #{satOdd} AND o.RealHomeWin = like #{endOdd}
AND o.FirstGuestWin = like #{satOdd} AND o.RealGuestWin = like #{endOdd}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc limit 50
