mapper.select:
select * from ( select o.OddsID,o.scheduleId,o.FirstHomeWin satWin,o.FirstStandoff satFlat,o.FirstGuestWin satLose, ifnull(o.RealHomeWin,o.FirstHomeWin) endWin, ifnull(o.RealStandoff,o.FirstStandoff) endFlat, ifnull(o.RealGuestWin,o.FirstGuestWin) endLose, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore > s.GuestScore then 3 when s.HomeScore = s.GuestScore then 1 else 0 end result, case when o.FirstHomeWin - o.FirstGuestWin <= 0 THEN abs(o.RealHomeWin - o.FirstHomeWin) ELSE abs(o.RealGuestWin - o.FirstGuestWin) END odd from europeodds o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid where o.CompanyID = #{companyId} and s.MatchState = -1
and sct.type = #{matchType}
AND o.FirstHomeWin = #{satWin} AND o.FirstGuestWin = #{satLose} AND o.FirstStandoff = #{satFlat}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc ) _temp where 1=1 and OddsID != #{oddsId}
and odd >= #{satOdds}
and odd < #{endOdds}
limit 50

select * from ( select o.scheduleId,o.FirstHomeWin satWin,o.FirstStandoff satFlat,o.FirstGuestWin satLose, ifnull(o.RealHomeWin,o.FirstHomeWin) endWin, ifnull(o.RealStandoff,o.FirstStandoff) endFlat, ifnull(o.RealGuestWin,o.FirstGuestWin) endLose, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore > s.GuestScore then 3 when s.HomeScore = s.GuestScore then 1 else 0 end result, case when o.FirstHomeWin - o.FirstGuestWin <= 0 THEN abs(o.RealHomeWin - o.FirstHomeWin) ELSE abs(o.RealGuestWin - o.FirstGuestWin) END odd from europeodds o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid where o.CompanyID = #{companyId} and s.MatchState = -1
AND sct.type = #{matchType}
AND o.FirstHomeWin = #{satOdd} AND o.RealHomeWin = #{endOdd}
AND o.FirstGuestWin = #{satOdd} AND o.RealGuestWin = #{endOdd}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc ) _temp where 1=1
and odd >= #{satChange}
and odd < #{endChange}
limit 50
