mapper.select:
select * from Score where 1=1
and SclassID=#{sclassid}

  and subSclassID=#{subsclassid}


  and Homeorguest=#{homeorguest}

and TeamID=#{teamid}
and MatchSeason=#{matchseason}

select ifnull(rank,0) rank, ifnull(matchCount,0) matchCount, ifnull(winCount,0) winCount, ifnull(flatCount,0) flatCount, ifnull(loseCount,0) loseCount, ifnull(goal,0) goal, ifnull(lost,0) lost, ifnull(goalDiff,0) goalDiff, ifnull(score,0) score from ( SELECT @sortnum :=@sortnum + 1 rank, winCount+flatCount+loseCount matchCount, winCount, flatCount, loseCount, goal, lost, goalDiff, score, teamid FROM ( SELECT teamid, sum(win_score * 3 + flat_score) as 'score', SUM(win_score) winCount, sum(flat_score) flatCount, sum(fail_score) loseCount, sum(total_homescore) goal, sum(total_guestscore) lost, sum(total_homescore-total_guestscore) goalDiff FROM score WHERE sclassid = #{sclassId} AND Matchseason = #{season}
AND subsclassId is null
AND subsclassId = #{subSclassID}
AND homeorguest = #{homeorguest}
GROUP BY teamid ORDER BY score desc, goalDiff desc, goal desc, teamid ASC ) s,(select @sortnum := 0) a ) temp where teamid = #{teamid}
