mapper.select:
select * from ( select o.OddsID,o.scheduleId,o.FirstUpodds satWin,o.FirstGoal satFlat,o.FirstGoal panlu,o.FirstDownodds satLose, ifnull(o.UpOdds_Real,o.FirstUpodds) endWin, ifnull(o.Goal_Real,o.FirstGoal) endFlat, ifnull(o.DownOdds_Real,o.FirstDownodds) endLose, s.MatchTime, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore - #{satFlat} > s.GuestScore then 3 when s.HomeScore - #{satFlat} = s.GuestScore then 1 else 0 end result, case when #{satFlat} > 0 THEN abs(UpOdds_Real - o.FirstUpodds) when #{satFlat} < 0 THEN abs(o.DownOdds_Real - o.FirstDownodds) ELSE (case when o.FirstUpodds <= o.FirstDownodds then abs(UpOdds_Real - o.FirstUpodds) ELSE abs(o.DownOdds_Real - o.FirstDownodds) END) END odd from letgoal o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN `letgoal_goal` lg on o.OddsID = lg.OddsID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid where o.CompanyID = #{companyId} and s.MatchState = -1 AND o.FirstUpodds = #{satWin} AND o.FirstDownodds = #{satLose} AND o.FirstGoal = #{satFlat}
and lg.goal_count = #{changeTimes}
and sct.type = #{matchType}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc limit 51 ) _temp where 1=1 AND OddsID != #{oddsId} ORDER BY MatchTime DESC limit 50

select scheduleId,satWin,satFlat,panlu,satLose,endWin,endFlat,endLose,MatchTime,matchName,homeName,homeScore,guestName,guestScore,result,odd from
(SELECT * from (select o.OddsID,o.scheduleId,o.FirstUpodds satWin,o.FirstGoal satFlat,o.FirstGoal panlu,o.FirstDownodds satLose, ifnull(o.UpOdds_Real,o.FirstUpodds) endWin, ifnull(o.Goal_Real,o.FirstGoal) endFlat, ifnull(o.DownOdds_Real,o.FirstDownodds) endLose, s.MatchTime, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore - #{satFlat} > s.GuestScore then 3 when s.HomeScore - #{satFlat} = s.GuestScore then 1 else 0 end result, case when #{satFlat} > 0 THEN abs(UpOdds_Real - o.FirstUpodds) when #{satFlat} < 0 THEN abs(o.DownOdds_Real - o.FirstDownodds) ELSE (case when o.FirstUpodds <= o.FirstDownodds then abs(UpOdds_Real - o.FirstUpodds) ELSE abs(o.DownOdds_Real - o.FirstDownodds) END) END odd from letgoal o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN `letgoal_goal` lg on o.OddsID = lg.OddsID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid where o.CompanyID = #{companyId} and s.MatchState = -1 AND o.FirstUpodds = #{satWin} AND o.FirstDownodds = #{satLose} AND o.FirstGoal = #{satFlat} and lg.goal_count = 0
sct.type = #{matchType}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc ) t where 1=1 AND OddsID != #{oddsId}
and odd >= #{odds.start}
and odd < #{odds.end}
limit 50 )
order by temp.MatchTime desc limit 50

select o.scheduleId,o.FirstUpodds satWin,o.FirstGoal satFlat,o.FirstGoal panlu,o.FirstDownodds satLose, ifnull(o.UpOdds_Real,o.FirstUpodds) endWin, ifnull(o.Goal_Real,o.FirstGoal) endFlat, ifnull(o.DownOdds_Real,o.FirstDownodds) endLose, s.MatchTime, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore - o.FirstGoal > s.GuestScore then 3 when s.HomeScore - o.FirstGoal = s.GuestScore then 1 else 0 end result from letgoal o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN `letgoal_goal` lg on o.OddsID = lg.OddsID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid where o.CompanyID = #{companyId} and s.MatchState = -1
AND o.FirstUpodds >= #{satSatOdd}
AND o.FirstUpodds < #{satEndOdd}
AND o.UpOdds_Real >= #{endSatOdd}
AND o.UpOdds_Real < #{endEndOdd}
AND o.FirstGoal = #{satGoal}
AND o.Goal_Real = #{endGoal}
AND lg.goal_count = #{changeTimes}
AND sct.type = #{matchType}
AND s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc limit 50

select scheduleId,satWin,satFlat,satLose,endWin,endFlat,endLose,MatchTime,matchName,homeName,homeScore,guestName,guestScore,result,odd from
(select scheduleId,satWin,satFlat,panlu,satLose,endWin,endFlat,endLose,MatchTime,matchName,homeName,homeScore,guestName,guestScore,result,odd from (select o.scheduleId,o.FirstUpodds satWin,o.FirstGoal satFlat,o.FirstGoal panlu,o.FirstDownodds satLose, ifnull(o.UpOdds_Real,o.FirstUpodds) endWin, ifnull(o.Goal_Real,o.FirstGoal) endFlat, ifnull(o.DownOdds_Real,o.FirstDownodds) endLose, s.MatchTime, sc.name_js matchName, t.name_j homeName, s.homescore homeScore, t2.name_j guestName, s.guestscore guestScore, case when s.HomeScore - o.FirstGoal > s.GuestScore then 3 when s.HomeScore - o.FirstGoal = s.GuestScore then 1 else 0 end result, case when o.FirstGoal > 0 THEN abs(UpOdds_Real - o.FirstUpodds) when o.FirstGoal < 0 THEN abs(o.DownOdds_Real - o.FirstDownodds) ELSE (case when o.FirstUpodds <= o.FirstDownodds then abs(UpOdds_Real - o.FirstUpodds) ELSE abs(o.DownOdds_Real - o.FirstDownodds) END) END odd from letgoal o LEFT JOIN `schedule` s on o.ScheduleID = s.ScheduleID LEFT JOIN `letgoal_goal` lg on o.OddsID = lg.OddsID LEFT JOIN sclass sc ON s.sclassid = sc.sclassid LEFT JOIN sclass_type sct ON sct.sclassid = sc.sclassid LEFT JOIN team t ON s.hometeamid = t.teamid LEFT JOIN team t2 ON s.guestteamid = t2.teamid where o.CompanyID = #{companyId} and s.MatchState = -1
AND o.FirstUpodds >= #{satSatOdd}
AND o.FirstUpodds < #{satEndOdd}
AND o.UpOdds_Real >= #{endSatOdd}
AND o.UpOdds_Real < #{endEndOdd}
AND o.FirstGoal = #{satGoal}
AND o.Goal_Real = #{endGoal}
and lg.goal_count = 0
and sct.type = #{matchType}
and s.MatchTime >= #{beginDate}
group by o.scheduleId,s.MatchTime order by s.MatchTime desc ) tt where 1=1
and odd >= #{odds.start}
and odd < #{odds.end}
limit 50 )
order by temp.MatchTime desc limit 50
